CREATE TRIGGER `trg_act_customer_info_real_cf_insert` AFTER INSERT ON `act_customer_info_real_cf` FOR EACH ROW BEGIN

DECLARE exitResult TINYINT;

SELECT
	1 INTO exitResult
FROM
	act_customer_info_real_cf
WHERE
	company_id = new.company_id
AND platform = new.platform
AND account_no != new.account_no
AND mobile = new.mobile
LIMIT 1;


IF exitResult IS NULL THEN
	SELECT
		1 INTO exitResult
	FROM
		act_customer_info_real_cf
	WHERE
		company_id = new.company_id
	AND platform = new.platform
	AND account_no != new.account_no
	AND email = new.email
	LIMIT 1;


END
IF;


IF exitResult IS NULL THEN
	SELECT
		1 INTO exitResult
	FROM
		act_customer_info_real_cf
	WHERE
		company_id = new.company_id
	AND platform = new.platform
	AND account_no != new.account_no
	AND id_number = new.id_number
	LIMIT 1;


END
IF;


IF exitResult = 1 THEN
	INSERT INTO act_duplicate_account_info_real_cf (
		company_id,
		platform,
		account_no,
		create_date,
		update_date,
		enable_flag,
		delete_flag,
		env,
		source
	) SELECT
		company_id,
		platform,
		account_no,
		create_date,
		update_date,
		enable_flag,
		delete_flag,
		env,
		source
	FROM
		(
			SELECT
				oriTbl.company_id company_id,
				oriTbl.platform platform,
				oriTbl.account_no account_no,
				now() create_date,
				now() update_date,
				'Y' enable_flag,
				'N' delete_flag,
				'real' env,
				oriTbl.source source
			FROM
				(
					SELECT
						company_id,
						platform,
						email
					FROM
						act_customer_info_real_cf
					WHERE
						email = new.email
					AND company_id = new.company_id
					AND platform = new.platform
					HAVING
						count(*) > 1
				) mainTbl
			LEFT JOIN act_customer_info_real_cf oriTbl ON oriTbl.company_id = mainTbl.company_id
			AND oriTbl.platform = mainTbl.platform
			AND mainTbl.email = oriTbl.email
			UNION ALL
				SELECT
					oriTbl.company_id company_id,
					oriTbl.platform platform,
					oriTbl.account_no account_no,
					now() create_date,
					now() update_date,
					'Y' enable_flag,
					'N' delete_flag,
					'real' env,
					oriTbl.source source
				FROM
					(
						SELECT
							company_id,
							platform,
							mobile
						FROM
							act_customer_info_real_cf
						WHERE
							mobile = new.mobile
						AND company_id = new.company_id
						AND platform = new.platform
						HAVING
							count(*) > 1
					) mainTbl
				LEFT JOIN act_customer_info_real_cf oriTbl ON oriTbl.company_id = mainTbl.company_id
				AND oriTbl.platform = mainTbl.platform
				AND mainTbl.mobile = oriTbl.mobile
				UNION ALL
					SELECT
						oriTbl.company_id company_id,
						oriTbl.platform platform,
						oriTbl.account_no account_no,
						now() create_date,
						now() update_date,
						'Y' enable_flag,
						'N' delete_flag,
						'real' env,
						oriTbl.source source
					FROM
						(
							SELECT
								company_id,
